mapper.select:
/* product.checkMaterial */ SELECT MATERIAL_IDX, MATERIAL_CODE, SAP_CODE, NAME, COMPANY_CODE, PLANT, PRICE, UNIT, EXTRA_UNIT, TYPE, IS_SAMPLE, IS_HIDDEN, IS_DELETE, MATERIAL_TYPE1, MATERIAL_TYPE2, MATERIAL_TYPE3, VERSION_NO, IS_LAST, DOC_NO, KEEP_CONDITION, WIDTH, LENGTH, HEIGHT, TOTAL_WEIGHT, STANDARD, ORIGIN, EXPIRATION_DATE FROM lab_material WHERE 1 = 1 AND IS_DELETE = 'N' AND IS_LAST = 'Y' AND IS_SAMPLE = 'Y'
AND MATERIAL_CODE LIKE CONCAT('%',#{matCode},'%')
AND SAP_CODE LIKE CONCAT('%',#{sapCode},'%')

mapper.insert:
/* product.insertProduct */
	INSERT INTO lab_product(
		PRODUCT_IDX
		, DOC_NO
		, PRODUCT_CODE
		, SAP_CODE
		, NAME
		, TITLE
		, CONTENTS
		, TOTAL_WEIGHT
		, STANDARD
		, KEEP_CONDITION
		, EXPIRATION_DATE
		, IS_NEW_MATERIAL
		, VERSION_NO
		
		, PRODUCT_TYPE1
		
		
		, PRODUCT_TYPE2
		
		
		, PRODUCT_TYPE3
		
		, IS_DELETE
		, IS_LAST
		, REG_USER
		, DOC_OWNER
		, STATUS
		, SCHEDULE_DATE
	)VALUES(
		#{idx}
		, (SELECT ifnull(MAX(lp.DOC_NO),0)+1 FROM lab_product lp WHERE 1 = 1)
		, #{productCode}
		, #{productSapCode}
		, #{productName}
		, #{title}
		, #{contents}
		, #{weight}
		, #{standard}
		, #{keepCondition}
		, #{expireDate}
		, #{newMat}
		, (SELECT ifnull(MAX(lp.VERSION_NO),0)+1 FROM lab_product lp WHERE 1 = 1 AND PRODUCT_CODE = #{productCode})
		
		, #{productType1}
		
		
		, #{productType2}
		
		
		, #{productType3}
		
		, 'N'
		, 'Y'
		, #{userId}
		, #{userId}
		, #{status}
		, #{scheduleDate}
	)

