mapper.select:
/* product.insertNewVersionCheck */
	SELECT
		count(PRODUCT_IDX) as cnt
	FROM 
		lab_product
	WHERE 1 = 1
		AND PRODUCT_CODE = #{productCode}
		AND DOC_NO = #{docNo}
		AND VERSION_NO = #{versionNo}

/* product.selectSearchProduct */ SELECT lp.* , CASE WHEN lp.STATUS = 'REG' THEN '등록' WHEN lp.STATUS = 'APPR' THEN '결재중' WHEN lp.STATUS = 'COMP' THEN '완료' WHEN lp.STATUS = 'COND_APPR' THEN '부분승인' END AS STATUS_TXT , (SELECT CATEGORY_NAME FROM lab_system_category WHERE 1 = 1 AND CATEGORY_IDX = lp.PRODUCT_TYPE1) as CATEGORY_NAME1 , (SELECT CATEGORY_NAME FROM lab_system_category WHERE 1 = 1 AND CATEGORY_IDX = lp.PRODUCT_TYPE2) as CATEGORY_NAME2 , (SELECT CATEGORY_NAME FROM lab_system_category WHERE 1 = 1 AND CATEGORY_IDX = lp.PRODUCT_TYPE3) as CATEGORY_NAME3
FROM lab_product lp WHERE 1 = 1 AND DOC_NO in ( SELECT tmp.DOC_NO FROM ( SELECT row_number() over (order by PRODUCT_IDX DESC) as rnum , DOC_NO FROM lab_product WHERE 1 = 1 AND IS_DELETE = 'N' AND IS_LAST = 'Y' AND DOC_OWNER = #{userId}
) tmp ) ORDER BY DOC_NO DESC, VERSION_NO DESC, PRODUCT_IDX DESC

mapper.delete:
/* product.deleteFileData */
	DELETE
	FROM lab_file
	WHERE 1 = 1
		AND FILE_IDX = #{fileIdx}

mapper.update:
/* product.updateProductData */
	UPDATE 
		lab_product
	SET
		NAME = #{productName}
		, TITLE = #{title}
		, PRODUCT_CODE = #{productCode}
		, TOTAL_WEIGHT = #{weight}
		, STANDARD = #{standard}
		, KEEP_CONDITION = #{keepCondition}
		, EXPIRATION_DATE = #{expireDate}
		, IS_NEW_MATERIAL = #{newMat}
		, PRODUCT_TYPE1 = #{productType1}
		, PRODUCT_TYPE2 = #{productType2}
		, PRODUCT_TYPE3 = #{productType3}
		, CONTENTS = #{contents}
		, SAP_CODE = #{productSapCode}
		
		, STATUS = #{status}
		
		, SCHEDULE_DATE = #{scheduleDate}
	WHERE 1 = 1
		AND PRODUCT_IDX = #{productIdx}

mapper.delete:
/* product.deleteProductMaterial */
	DELETE
	FROM lab_product_material
	WHERE 1 = 1
		AND PRODUCT_IDX = #{productIdx}

/* product.deleteFileType */
	DELETE
	FROM lab_file_type
	WHERE 1 = 1
		AND DOC_IDX = #{productIdx}
		AND DOC_TYPE = #{docType}
