mapper.select:
select count(*) from ( select row_number() over (order by nNo desc) as rn ,(select count(*) from comment where tbKey=nNo and tbType='team') as commentCount ,nNo ,title ,content ,getUserName(regUserId) as userName ,regDate ,regUserId ,(select count(*) from fileManager where tbType='team' AND tbKey = nNo) as fileCount ,departName FROM departNotice WHERE departName = '${deptCode}'
AND (getUserName(regUserId) LIKE concat('%',#{keyword},'%'))
AND (title LIKE '%' || #{keyword} || '%')
AND (getUserName(regUserId) LIKE concat('%',#{keyword},'%') OR title LIKE concat('%',#{keyword},'%'))
AND DATE_FORMAT(regDate,'%Y-%m-%d') between #{startDate} and #{endDate}
)a

select * from ( select row_number() over (order by nNo desc) as rn ,(select count(*) from comment where tbKey=nNo and tbType='team') as commentCount ,nNo ,title ,content ,getUserName(regUserId) as userName ,regDate ,regUserId ,(select count(*) from fileManager where tbType='team' AND tbKey = nNo) as fileCount ,departName FROM departNotice WHERE departName = '${deptCode}'
AND (getUserName(regUserId) LIKE concat('%',#{keyword},'%'))
AND (title LIKE '%' || #{keyword} || '%')
AND (getUserName(regUserId) LIKE concat('%',#{keyword},'%') OR title LIKE concat('%',#{keyword},'%'))
AND DATE_FORMAT(regDate,'%Y-%m-%d') between #{startDate} and #{endDate}
)a where rn between #{startRow} and #{endRow} order by regDate desc

mapper.update:
update departNotice set 
title=#{title},
content=#{contents},
modUserId=#{modUserId}
where nNo=#{nNo}
