mapper.insert:
INSERT 
INTO lab_approval_line_header(
	LINE_IDX
	, NAME
	, DOC_TYPE
	, REG_USER
	, REG_DATE
	, IS_DELETE
) VALUES (
	#{lineIdx}
	, #{apprLineName}
	, #{docType}
	, #{userId}
	, sysdate()
	, 'N'
)

mapper.select:
SELECT LINE_IDX , NAME , DOC_TYPE , REG_USER , REG_DATE , IS_DELETE FROM lab_approval_line_header WHERE 1 = 1
AND DOC_TYPE = #{docType}
AND REG_USER = #{userId} AND IS_DELETE = 'N'

mapper.insert:
INSERT 
INTO lab_approval_header(
	APPR_IDX
	, DOC_IDX
	, DOC_TYPE
	, TOTAL_STEP
	, CURRENT_STEP
	, CURRENT_USER_ID
	, TITLE
	, REG_USER
	, REG_DATE
	, COMMENT
	, LAST_STATUS
) values (
	#{apprIdx}
	, #{docIdx}
	, #{docType}
	, #{totalStep}
	, #{currentStep}
	, #{currentUser}
	, #{title}
	, #{userId}
	, sysdate()
	, #{apprComment}
	, #{status}
)

mapper.update:
UPDATE 
	lab_product
SET
	STATUS = #{status}
WHERE 1 = 1
	AND PRODUCT_IDX = #{docIdx}

mapper.select:
/* approval2.selectApprHeaderData */ SELECT lah.APPR_IDX , lah.DOC_IDX , lah.DOC_TYPE , lah.TOTAL_STEP , lah.CURRENT_STEP , lah.CURRENT_USER_ID , lah.TITLE , lah.REG_USER , lah.REG_DATE , lah.LAST_STATUS , lah.COMMENT , lah.UPDATE_DATE , lai.ITEM_IDX , lai.APPR_NO , lai.TARGET_USER_ID , lai.STATUS , lai.COMMENT , lai.REG_DATE FROM lab_approval_header lah , lab_approval_item lai WHERE 1 = 1
AND lah.APPR_IDX = #{apprIdx}
AND lah.DOC_IDX = #{docIdx}
AND lah.DOC_TYPE = #{docType}
AND lah.LAST_STATUS = #{lastStatus}
AND lah.APPR_IDX = lai.APPR_IDX AND lah.CURRENT_STEP = lai.APPR_NO

	/*	approval2.selectDocData */
		SELECT 
			*
		FROM 
		
			
			lab_product
			
      		
      		lab_menu
      		
      		
      		lab_design
      		
      		
      		lab_business_trip
      		
      	
		WHERE 1 = 1
			AND IS_DELETE = 'N'
			
				
				AND PRODUCT_IDX = #{docIdx}	
				
	      		
	      		AND MENU_IDX = #{docIdx}
	      		
	      		
	      		AND DESIGN_IDX = #{docIdx}
	      		
	      		
	      		AND TRIP_IDX = #{docIdx}

mapper.update:
/*	approval2.updateApprStatus */
	UPDATE 
		lab_approval_header
	SET
		LAST_STATUS	= #{status}
		, UPDATE_DATE = sysdate()
	WHERE 1 = 1
		AND APPR_IDX = #{apprIdx}

	/*	approval2.updateDocStatus */
		UPDATE 
		
			
			lab_product
			
      		
      		lab_menu
      		
      		
      		lab_design
      		
      		
      		lab_business_trip
      		
      	
		SET
			STATUS = #{docStatus}
		WHERE 1 = 1
		
			
			AND PRODUCT_IDX = #{docIdx}	
			
      		
      		AND MENU_IDX = #{docIdx}
      		
      		
      		AND DESIGN_IDX = #{docIdx}
      		
      		
      		AND TRIP_IDX = #{docIdx}

/*	approval2.approvalSubmitItem */
	UPDATE 
		lab_approval_item
	SET	
		STATUS = #{apprStatus}
		, COMMENT = #{comment}
		, REG_DATE = sysdate()
	WHERE 1 = 1
		AND ITEM_IDX = #{itemIdx}
		AND APPR_IDX = #{apprIdx}

/*	approval2.updateApprUser */
	UPDATE 
		lab_approval_header
	SET
		CURRENT_STEP = #{currentStep}
		, CURRENT_USER_ID = #{currentUser}
		, LAST_STATUS = #{lastStatus}
	WHERE 1 = 1
		AND APPR_IDX = #{apprIdx}
