mapper.select:
    /*code.groupCount*/
	  	SELECT	COUNT(GROUP_CODE) CNT
	  	FROM 	lab_code_group
	  	WHERE 	1=1
	  	AND GROUP_CODE = #{groupCode}

mapper.insert:
/*code.groupInsert*/
	INSERT
		INTO lab_code_group
	(
		GROUP_CODE
		, GROUP_NAME
		, DESCRIPTION
		, IS_DELETE
		, REG_USER_ID
		, REG_DATE
	) VALUES (
		#{groupCode}
		, #{groupName}
		, #{description}
		, 'N'
		, ''
		, sysdate()
	)

mapper.update:
/*code.groupUpdate*/
	UPDATE lab_code_group
	SET
		GROUP_NAME = #{groupName}
		,DESCRIPTION = #{description}
		,UPDATE_USER_ID = ''
		,UPDATE_DATE = sysdate()
	WHERE GROUP_CODE = #{groupCode}

mapper.select:
/*code.groupItemCount*/
  	SELECT COUNT(GROUP_CODE) CNT
    FROM lab_code_item
    WHERE 	1=1
  	AND GROUP_CODE = #{groupCode}
  	AND IS_DELETE = 'N'

/*code.itemCount*/
  	SELECT COUNT(ITEM_CODE) CNT
    FROM lab_code_item
    WHERE 	1=1
  	AND GROUP_CODE = #{groupCode}
  	AND ITEM_CODE = #{itemCode}

mapper.insert:
/*code.itemInsert*/
	INSERT
		INTO lab_code_item
	(
		GROUP_CODE
		, ITEM_CODE
		, ITEM_NAME
		, DESCRIPTION
		, ORDER_NO
		, IS_DELETE
		, REG_USER_ID
		, REG_DATE
	) VALUES (
		#{groupCode}
		, #{itemCode}
		, #{itemName}
		, #{description}
		, (SELECT ifnull(max(a.ORDER_NO),0)+1 FROM lab_code_item a WHERE a.GROUP_CODE = #{groupCode})
		, 'N'
		, ''
		, sysdate()
	)

mapper.update:
/*code.itemUpdate*/
	UPDATE lab_code_item
	SET
		ITEM_NAME = #{itemName}
		,DESCRIPTION = #{description}
		,UPDATE_USER_ID = ''
		,UPDATE_DATE = sysdate()
	WHERE GROUP_CODE = #{groupCode}
	AND ITEM_CODE = #{itemCode}

/*code.itemDelete*/
	UPDATE lab_code_item
	SET
		IS_DELETE = 'Y'
		,UPDATE_USER_ID = ''
		,UPDATE_DATE = sysdate()
	WHERE GROUP_CODE = #{groupCode}
	AND ITEM_CODE = #{itemCode}

	/*code.itemOrderUpdateAjax*/
		UPDATE lab_code_item
		SET
		
			
			ORDER_NO = (ORDER_NO-1)
			
      		
      		ORDER_NO = (ORDER_NO+1)
      			
      	
		WHERE GROUP_CODE = #{groupCode}
		AND ITEM_CODE = #{itemCode}
		AND IS_DELETE = 'N'
