mapper.select:
/*common.plant*/ SELECT plantCode ,plantName ,companyCode ,companyNo FROM plant WHERE 1 =1
AND companyCode = #{companyCode}
AND plantCode = #{plantCode}
ORDER BY companyCode DESC , plantCode ASC

/*common.plantLine*/ SELECT companyCode ,plantCode ,lineCode ,lineName ,isDel FROM plantLine WHERE 1 =1
AND companyCode = #{companyCode}
AND plantCode = #{plantCode}
ORDER BY lineName ASC

/*common.codeList*/ SELECT itemCode ,itemName FROM codeItem WHERE groupCode = #{groupCode}
AND itemCode like concat(#{itemCode},'%')
AND isDelete = 'N' ORDER BY orderNo ASC

/*common.searchUserId*/ SELECT userId ,userName ,userGrade ,getCodeName('GRADE',userGrade) AS userGradeName ,DATE_FORMAT(regDate,'%Y-%m-%d') as regDate ,userCode ,email ,deptCode ,getCodeName('DEPT',deptCode) AS deptCodeName ,teamCode ,getCodeName('TEAM',teamCode) AS teamCodeName ,titleCode ,userType ,isAdmin ,isDelete FROM userTable2 WHERE 1 = 1
AND userId = #{userId}
AND deptCode = #{deptCode}
AND teamCode = #{teamCode}

/*common.searchUserList*/ SELECT t.* FROM ( SELECT row_number() over (order by userId desc) as rnum ,userId ,userPwd ,userName ,userGrade ,getCodeName('GRADE',userGrade) AS userGradeName ,DATE_FORMAT(regDate,'%Y-%m-%d') AS regDate ,userCode ,email ,deptCode ,getCodeName('DEPT',deptCode) AS deptCodeName ,teamCode ,getCodeName('TEAM',teamCode) AS teamCodeName ,titleCode ,userType ,isAdmin ,isDelete FROM userTable2 WHERE 1=1
AND (userId like concat('%',#{keyword},'%') OR userName like concat('%',#{keyword},'%'))
) t WHERE rnum between #{startRow} and #{endRow} ORDER BY userId desc

/*common.searchUserList2*/ SELECT userId ,userPwd ,userName ,userGrade ,getCodeName('GRADE',userGrade) AS userGradeName ,DATE_FORMAT(regDate,'%Y-%m-%d') AS regDate ,userCode ,email ,deptCode ,getCodeName('DEPT',deptCode) AS deptCodeName ,teamCode ,getCodeName('TEAM',teamCode) AS teamCodeName ,titleCode ,userType ,isAdmin ,isDelete FROM userTable2 WHERE 1=1
AND (userId like concat('%',#{keyword},'%') OR userName like concat('%',#{keyword},'%'))
AND isDelete = 'N' ORDER BY userId desc

mapper.insert:
    /*	common.insertNotification 알림정보 등록 */
    INSERT INTO notification(
    	targetUserId
		,message
		,regUserId
		,regDate
		,isRead
		,typeText
		,type ) 
	VALUES(
		#{targetUserId}
		, #{message}
		, #{regUserId}
		, sysdate()
		, #{isRead}
		, #{typeText}
		, #{type}
	)

mapper.select:
/*common.userInfo*/ SELECT userId ,userName ,email FROM userTable2 WHERE 1=1 AND userId IN
#{userId}
AND isDelete = 'N' ORDER BY userId desc

mapper.insert:
    /*	common.insertPrintLog 프린트/다운로드 로그 */
    INSERT INTO print_log(
    	apprNo
      	,tbType
      	,tbKey
      	,userId
      	,printDate
      	,type ) 
	VALUES(
		#{apprNo}
		, #{tbType}
		, #{tbKey}
		, #{userId}
		, sysdate()
		, #{type}
	)

mapper.select:
/*common.docCount 사용자 문서 갯수*/ SELECT type, sum(docCount) as docCount FROM docCountView WHERE 1 = 1
AND userId in (SELECT userId FROM userTable2 WHERE deptCode = #{deptCode} AND isDelete = 'N')
AND userId = #{userId}
GROUP BY type

/*common.docCount 사용자 문서 상태 갯수*/ SELECT type, state, sum(docCount) as docCount FROM docStateView WHERE 1 = 1
AND userId in (SELECT userId FROM userTable2 WHERE deptCode = #{deptCode} AND isDelete = 'N')
AND userId = #{userId}
GROUP BY type, state

SELECT companyCode , plantCode , storageCode , storageName FROM storageLocation WHERE 1=1
AND companyCode = #{companyCode}
AND plantCode = #{plantCode}
ORDER BY storageCode
