mapper.select:
SELECT COUNT(*) FROM material WHERE isDelete != 'Y' AND isHidden != 'Y'
AND type like concat('%',#{search.itemType},'%')
AND type in ('B', 'R')
AND company = #{search.companyCode}
AND plant = #{search.plant}
AND ( sapCode LIKE concat('%',#{search.searchValue},'%') OR name LIKE concat('%',#{search.searchValue},'%') )

SELECT getItemDispName(company, sapCode) , imNo itemImNo , sapCode itemSAPCode , name itemName , plant , IFNULL(getPlantName2(company, plant), '') plantName , ROUND(IFNULL(price, 0),5) as itemPrice , regUserId , unit itemUnit , exitUnit itemExitUnit , isDelete , isSample , type itemType , DATE_FORMAT(regDate,'%Y-%m-%d') AS regDate , modUserId , company companyCode , getItemDispName(company,sapCode) AS itemDispName , IFNULL(supplyDate, DATE_FORMAT(regDate,'%Y-%m-%d')) AS supplyDate , IFNULL(supplyCompany, '') AS supplyCompany FROM ( SELECT ROW_NUMBER() OVER(ORDER BY supplyDate DESC, regDate DESC, imNo DESC) rn, material.* FROM material WHERE isDelete != 'Y' AND isHidden != 'Y'
AND type like concat('%',#{search.itemType},'%')
AND type in ('B', 'R')
AND company = #{search.companyCode}
AND plant = #{search.plant}
AND ( sapCode LIKE concat('%',#{search.searchValue},'%') OR name LIKE concat('%',#{search.searchValue},'%') )
) a

SELECT *
FROM material 
WHERE isDelete != 'Y' AND isHidden != 'Y'
  AND sapCode = #{sapCode}

	
		AND type like #{type}
	
	
		AND type in ('B', 'R')
	

  AND company = #{company}
  AND plant = #{plant}
