mapper.select:
/*devdocManagement.devDocList*/ SELECT ddNo, t.docNo, t.docVersion, productCode, productName, explanation, isLatest, regDate, regUserName, modDate, modUserName, launchDate, regUserId, modUserId, versionUpMemo, isClose, productCategoryName, docType, dNo, ifnull(stateCnt1, 0) as stateCnt1, ifnull(stateCnt4, 0) as stateCnt4, productType1Text, productType2Text, productType3Text FROM ( (SELECT ddNo, docNo, docVersion, productCode, productName, explanation, isLatest, DATE_FORMAT(regDate,'%Y-%m-%d') as regDate, getUserName(regUserId) as regUserName, DATE_FORMAT(modDate,'%Y-%m-%d') as modDate, getUserName(modUserId) as modUserName, launchDate, regUserId, modUserId, versionUpMemo, isClose, getCodeName('PRODCAT',productCategory) as productCategoryName, getCodeName('PRODCAT1', productType1) as productType1Text, getCodeName('PRODCAT2', productType2) as productType2Text, getCodeName('PRODCAT3', productType3) as productType3Text FROM devDoc WHERE 1 =1
AND regUserId = #{userId}
AND ${searchField} LIKE concat('%',#{searchValue},'%')
AND isLatest = '1' AND isHidden != 'Y' ) t LEFT OUTER JOIN (SELECT docNo, docVersion, max(docType) as docType, max(dNo) as dNo, sum(case when state = 1 then 1 else 0 end) as stateCnt1, sum(case when state = 4 then 1 else 0 end) as stateCnt4, max(docProdName) as docProdName FROM manufacturingProcessDoc GROUP BY docNo, docVersion )m ON t.docNo = m.docNo AND t.docVersion = m.docVersion ) ORDER BY t.docNo, t.docVersion ASC

/*devdocManagement.menuDocList*/ SELECT pNo ,productName ,getPlantName(companyCode, plant) as plantName ,getCodeName('PRODCAT',productCategory) as productCategoryName ,getCompanyName(companyCode) as companyName ,regUserId ,getUserName(regUserId) as regUserName FROM productDesignDoc WHERE 1 = 1
AND regUserId = #{userId}
AND ${searchField} LIKE concat('%',#{searchValue},'%')

mapper.update:
	/*	devdocManagement.userChange 제품개발 문서 담당자 변경 */
	UPDATE devDoc 
	SET 
      regUserId =  #{userId}
	WHERE	1 = 1
		AND docNo = #{docNo}

	/*	devdocManagement.userChangeMenuDoc 제품설계서 담당자 변경 */
	UPDATE productDesignDoc 
	SET 
      regUserId =  #{userId}
	WHERE	1 = 1
		AND pNo = #{pNo}

mapper.insert:
/* user.insertChangeLog   */
INSERT INTO userChange_log(
	docNo
    ,beforeUserId
    ,changeUserId
    ,regDate
    ,docName
)	VALUES (
	#{docNo}
	,#{regUserId}
	,#{userId}
	,sysdate()
	,#{docName}
)

mapper.update:
UPDATE 	devDoc 
SET launchDate='${launchDate}'
WHERE docNo =${docNo}
AND docVersion =${docVersion}
