mapper.select:
/* menu.menu */
	SELECT 
		LPAD(FN_NEXTVAL('menu_code_seq'), '3' , '0') as code

/* menu.checkMaterial */ SELECT MATERIAL_IDX, MATERIAL_CODE, SAP_CODE, NAME, COMPANY_CODE, PLANT, PRICE, UNIT, EXTRA_UNIT, TYPE, IS_SAMPLE, IS_HIDDEN, IS_DELETE, MATERIAL_TYPE1, MATERIAL_TYPE2, MATERIAL_TYPE3, VERSION_NO, IS_LAST, DOC_NO, KEEP_CONDITION, WIDTH, LENGTH, HEIGHT, TOTAL_WEIGHT, STANDARD, ORIGIN, EXPIRATION_DATE FROM lab_material WHERE 1 = 1 AND IS_DELETE = 'N' AND IS_LAST = 'Y' AND IS_SAMPLE = 'Y'
AND MATERIAL_CODE LIKE CONCAT('%',#{matCode},'%')
AND SAP_CODE LIKE CONCAT('%',#{sapCode},'%')

mapper.insert:
/* menu.insertMenu */
	INSERT INTO lab_menu(
		MENU_IDX
		, DOC_NO
		, MENU_CODE
		, SAP_CODE
		, NAME
		, TITLE
		, CONTENTS
		, IS_NEW_MATERIAL
		, VERSION_NO
		
		, MENU_TYPE1
		
		
		, MENU_TYPE2
		
		
		, MENU_TYPE3
		
		, IS_DELETE
		, IS_LAST
		, REG_USER
		, DOC_OWNER
		, STATUS
		, SCHEDULE_DATE
	)VALUES(
		#{idx}
		, (SELECT ifnull(MAX(lp.DOC_NO),0)+1 FROM lab_menu lp WHERE 1 = 1)
		, #{menuCode}
		, #{menuSapCode}
		, #{menuName}
		, #{title}
		, #{contents}
		, #{newMat}
		
			
			, (SELECT ifnull(MAX(lp.VERSION_NO),0)+1 FROM lab_menu lp WHERE 1 = 1 AND MENU_CODE = #{menuCode})
			
			
			, 1
			
		
		
		, #{menuType1}
		
		
		, #{menuType2}
		
		
		, #{menuType3}
		
		, 'N'
		, 'Y'
		, #{userId}
		, #{userId}
		, #{status}
		, #{scheduleDate}
	)

/* menu.insertNewVersionMenu */
	INSERT INTO lab_menu(
		MENU_IDX
		, DOC_NO
		, MENU_CODE
		, SAP_CODE
		, NAME
		, TITLE
		, CONTENTS
		, IS_NEW_MATERIAL
		, VERSION_NO
		
		, MENU_TYPE1
		
		
		, MENU_TYPE2
		
		
		, MENU_TYPE3
		
		, IS_DELETE
		, IS_LAST
		, REG_USER
		, DOC_OWNER
		, STATUS
		, SCHEDULE_DATE
	)VALUES(
		#{idx}
		, #{docNo}
		, #{menuCode}
		, #{menuSapCode}
		, #{menuName}
		, #{title}
		, #{contents}
		, #{newMat}
		, #{versionNo}
		
		, #{menuType1}
		
		
		, #{menuType2}
		
		
		, #{menuType3}
		
		, 'N'
		, #{isLast}
		, #{userId}
		, #{userId}
		, #{status}
		, #{scheduleDate}
	)

mapper.select:
/* menu.insertNewVersionCheck */
	SELECT
		count(MENU_IDX) as cnt
	FROM 
		lab_menu
	WHERE 1 = 1
		AND MENU_CODE = #{menuCode}
		AND DOC_NO = #{docNo}
		AND VERSION_NO = #{versionNo}

/* menu.selectSearchMenu */ SELECT lp.* , CASE WHEN lp.STATUS = 'REG' THEN '등록' WHEN lp.STATUS = 'APPR' THEN '결재중' WHEN lp.STATUS = 'COMP' THEN '완료' WHEN lp.STATUS = 'COND_APPR' THEN '부분승인' END AS STATUS_TXT , (SELECT CATEGORY_NAME FROM lab_system_category WHERE 1 = 1 AND CATEGORY_IDX = lp.MENU_TYPE1) as CATEGORY_NAME1 , (SELECT CATEGORY_NAME FROM lab_system_category WHERE 1 = 1 AND CATEGORY_IDX = lp.MENU_TYPE2) as CATEGORY_NAME2 , (SELECT CATEGORY_NAME FROM lab_system_category WHERE 1 = 1 AND CATEGORY_IDX = lp.MENU_TYPE3) as CATEGORY_NAME3
FROM lab_menu lp WHERE 1 = 1 AND DOC_NO in ( SELECT tmp.DOC_NO FROM ( SELECT row_number() over (order by MENU_IDX DESC) as rnum , DOC_NO FROM lab_menu WHERE 1 = 1 AND IS_DELETE = 'N' AND IS_LAST = 'Y' AND DOC_OWNER = #{userId}
) tmp ) ORDER BY DOC_NO DESC, VERSION_NO DESC, MENU_IDX DESC

mapper.delete:
/* menu.deleteFileData */
	DELETE
	FROM lab_file
	WHERE 1 = 1
		AND FILE_IDX = #{fileIdx}

mapper.update:
/* menu.updateMenuData */
	UPDATE 
		lab_menu
	SET
		NAME = #{menuName}
		, TITLE = #{title}
		, MENU_CODE = #{menuCode}
		, IS_NEW_MATERIAL = #{newMat}
		, MENU_TYPE1 = #{menuType1}
		, MENU_TYPE2 = #{menuType2}
		, MENU_TYPE3 = #{menuType3}
		, CONTENTS = #{contents}
		, SAP_CODE = #{menuSapCode}
		
		, STATUS = #{status}
		
		, SCHEDULE_DATE = #{scheduleDate}
	WHERE 1 = 1
		AND MENU_IDX = #{menuIdx}

mapper.delete:
/* menu.deleteMenuMaterial */
	DELETE
	FROM lab_menu_material
	WHERE 1 = 1
		AND MENU_IDX = #{menuIdx}

/* menu.deleteFileType */
	DELETE
	FROM lab_file_type
	WHERE 1 = 1
		AND DOC_IDX = #{menuIdx}
		AND DOC_TYPE = #{docType}
