mapper.select:
/** report.reportCount */
	SELECT
		count(rNo) 
	FROM (
		SELECT
			report.*
			, (SELECT deptCode FROM usertable2 WHERE userid = report.regUserId) userDept
		FROM report
	) a
	WHERE 1 = 1
	
		AND category1 = #{category1}
	
	
		AND (title LIKE '%'+#{keyword}+'%' OR prdTitle LIKE '%'+#{keyword}+'%' OR convert(varchar(10),reportDate,120) LIKE '%'+#{keyword}+'%')
	
	
		AND userDept = #{deptCode}
	
	
		AND subCategory = #{subCategory}

/** report.reportList */ SELECT rNo ,rNo as reportKey ,category1 ,dbo.getCodeName('REPORTCATEGORY1', category1) as category1Name , title , prdTitle , userName , regDate , regUserId , fileCount , reportDate , testTitle , seminarTitle , subCategory , ISNULL(dbo.getCodeName(category1, subCategory), '') as subCategoryName , regularTitle FROM ( SELECT row_number() over (order by rNo desc) as rn , rNo , category1 , title , prdTitle , dbo.getUserName(regUserId) as userName , CONVERT(CHAR(10), regDate, 23) as regDate , regUserId , (select count(*) from fileManager where tbType='report' AND tbKey = rNo) as fileCount , CONVERT(CHAR(10), reportDate, 23) as reportDate , testTitle , seminarTitle , subCategory , regularTitle FROM ( SELECT report.* , (SELECT deptCode FROM usertable2 WHERE userid = report.regUserId) userDept FROM report ) a WHERE 1 = 1
AND category1 = #{category1}
AND (title LIKE '%'+#{keyword}+'%' OR prdTitle LIKE '%'+#{keyword}+'%' OR convert(varchar(10),reportDate,120) LIKE '%'+#{keyword}+'%')
AND userDept = #{deptCode}
AND subCategory = #{subCategory}
) t WHERE rn between #{startRow} and #{endRow} ORDER BY regDate DESC;

mapper.insert:
  	/*report.insert*/
  	
		INSERT 
		INTO report(
			title
			,regUserId
  		    ,category1
		    ,regDate
		    ,prdTitle
		    ,adviserPrd
		    ,prdFeature
		    ,reportDate
		    ,isConfirm
		    ,isRelease
		    ,result
		    ,visitPurpose
		    ,visitPlace
		    ,visitUser
		    ,visitTime
		    ,testPurpose
		    ,testDate
		    ,testObject
		    ,seminarDate
		    ,seminarHost
		    ,seminarContent
		    ,subCategory
		    ,regularTitle 
			) 
		VALUES (
			#{title}
			,#{regUserId}
			,#{category1}
		    ,getDate()
		    ,#{prdTitle}
		    ,#{adviserPrd}
		    ,#{prdFeature}
		    ,#{reportDate}
		    ,#{isConfirm}
		    ,#{isRelease}
		    ,#{result}
		    ,#{visitPurpose}
		    ,#{visitPlace}
		    ,#{visitUser}
		    ,#{visitTime}
		    ,#{testPurpose}
		    ,#{testDate}
		    ,#{testObject}
		    ,#{seminarDate}
		    ,#{seminarHost}
		    ,#{seminarContent}
		    ,#{subCategory}
		    ,#{regularTitle}
		)
			select SCOPE_IDENTITY()

mapper.delete:
  	/*report.deleteReportBom*/
  		DELETE 
		FROM reportBom
		WHERE 1 = 1
			AND rNo = #{rNo}

mapper.update:
UPDATE 
	report
SET state = #{state}
WHERE rNo = #{rNo}

    	UPDATE		
    		report 
		SET	
			title = #{title} 
			,modUserId = #{modUserId}
  		    ,category1 = #{category1}
		    ,regDate = GETDATE()
