		    ,regularTitle = #{regularTitle}
		WHERE 		
			rNo = #{rNo}

mapper.delete:
  	/*report.deleteReportBom*/
  		DELETE 
		FROM reportMix
		WHERE 1 = 1
			AND rNo = #{rNo}

  	/*report.deleteReportBom*/
  		DELETE 
		FROM reportMixItem
		WHERE 1 = 1
			AND rNo = #{rNo}

mapper.select:
   	/*report.getSubCategory*/
    	SELECT a.* 
			, ISNULL((SELECT COUNT(1) FROM report WHERE category1 = a.groupCode AND subCategory = a.itemCode GROUP BY subCategory), 0) subCategoryCnt 
		FROM codeItem a 
		WHERE groupCode = #{category1}
