mapper.update:
UPDATE 
	lab_business_trip
SET 
	TRIP_TYPE = #{tripType}
	, TITLE = #{title}
	, DEPT = #{dept}
	, POSITION = #{position}
	, NAME = #{name}
	, TRIP_PURPOSE = #{tripPurpose}
	, TRIP_START_DATE = #{tripStartDate}
	, TRIP_END_DATE = #{tripEndDate}
	, TRIP_DESTINATION = #{tripDestination}
	, TRIP_TRANSIT = #{tripTransit}
	, CONTENTS = #{tripContents}
	, TRIP_COST = #{tripCost}
	, OVER_REASON = #{overReason}
	, TRIP_EFFECT = #{tripEffect}
	
	, STATUS = #{status}
	
WHERE 1 = 1
	AND TRIP_IDX = #{idx}

